<?php	function print_now($archivo)	{		//ip de impresion de produccion 		$ip			= $_SESSION['ip_printer_default'] ;		$sql_print	= pg_exec("select print_netbios, print_name from list_impresoras where trim(print_server) = trim('".$ip."')");		$rs_print	= pg_numrows($sql_print);		if ($rs_print != 0)		{			$A				= pg_fetch_array($sql_print, 0);			$print_netbios	= $A[0];			$print_name		= $A[1];		}		else		{			$rs = pg_exec("select par_valor as print_netbios from int_parametros where par_nombre ='print_netbios' ");			$A = pg_fetch_array($rs,0);			$print_netbios =  $A["print_netbios"];						$rs = pg_exec("select par_valor as print_name from int_parametros where par_nombre ='print_name' ");			$A = pg_fetch_array($rs,0);			$print_name =  $A["print_name"];		}				$a="smbclient //".$print_netbios."/".$print_name." -c 'print ".$archivo."' -N -I ".$ip." ";		exec($a);	}?>
